		0x0001: { n:"BrtCellBlank", f:parse_BrtCellBlank },
		0x0002: { n:"BrtCellRk", f:parse_BrtCellRk },
		0x0003: { n:"BrtCellError", f:parse_BrtCellError },
		0x0004: { n:"BrtCellBool", f:parse_BrtCellBool },
